import React,{useState} from 'react';
import {useNavigate} from 'react-router-dom';

const SignUp = () => {
    const [name, setName]=useState('');
    const [email, setEmail]=useState('');
    const [password, setPassword]=useState('');

    const navigate = useNavigate();

    const collectData= async () => {
        console.warn(name,email,password);

        let result = fetch("http://localhost:5000/register", {
            method: "POST",
            body: JSON.stringify({name,email,password}),  // Send the data in the request body.
            headers:{
                'Content-Type':'application/json'  // Specify the content type.
            }
        });

        // Convert the response to JSON.
        result = await result.json();
        // Log the result.
        console.warn(result);
        navigate('/');
    }

    return (
        <div className='signup'>
            <h1>Register</h1>
            <input className='inputBox' type='text' placeholder='Enter Username'
            value={name} onChange={(e)=>setName(e.target.value)}
            />
            <input className='inputBox' type='text' placeholder='Enter Email'
            value={email} onChange={(e)=>setEmail(e.target.value)}
            />
            <input className='inputBox' type='password' placeholder='Enter Password'
            value={password} onChange={(e)=>setPassword(e.target.value)}
            />
            <button onClick={collectData} className='registerButton' type='button'>Sign Up</button>
        </div>
    )
}

export default SignUp;
