import React, { useState } from 'react';
import logo from '../components/logo.png';
import './About.css'; // Import the CSS file for styling

const About = () => {
    const [activeSection, setActiveSection] = useState(0);

    const sections = [
        {
            title: 'About Us',
            content: (
                <>
                    <p>Welcome to our car dealership! We offer a wide selection of vehicles for both sale and rental. Our mission is to provide you with the best cars at the most affordable prices.</p>
                    <ul>
                        <li>At <strong>McQue Motors</strong>, we're more than just a dealership; we're dedicated to providing an exceptional car-buying experience grounded in integrity and quality. Based in Sandton, Gauteng, our commitment extends beyond selling vehicles to ensuring that every customer drives away with complete satisfaction.</li>
                        <li>Our streamlined sales process includes a wide range of high-quality new and used cars, all priced affordably to fit various budgets. We offer straightforward trade-in options and transparent finance arrangements to make your purchase as smooth as possible.</li>
                    </ul>
                </>
            ),
        },
        {
            title: 'Our Vision',
            content: (
                <p>Our vision is to be the premier choice for car buyers in Gauteng, known for our commitment to quality, affordability, and exceptional service.</p>
            ),
        },
        {
            title: 'Why Choose Us?',
            content: (
                <ul>
                    <li><strong>Top-Quality Vehicles:</strong> We pride ourselves on offering a diverse selection of high-quality cars, rigorously inspected to meet our standards.</li>
                    <li><strong>Exceptional Customer Service:</strong> Our team is dedicated to providing a friendly, transparent, and efficient car-buying experience.</li>
                    <li><strong>Affordable Prices:</strong> We strive to offer competitive pricing that makes owning a great car within reach for everyone.</li>
                </ul>
            ),
        },
        {
            title: 'Our Mission',
            content: (
                <p>From day one, our mission has been clear: to deliver exceptional vehicles and unparalleled service while maintaining the highest standards of integrity.</p>
            ),
        },
        {
            title: 'Our Location',
            content: (
                <iframe
                    title="dealership-map"
                    src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3153.130968795787!2d144.96315781531635!3d-37.81421797975198!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x6ad65d43e0e1f4a3%3A0x5045675218ce540!2sMelbourne%20VIC%2C%20Australia!5e0!3m2!1sen!2sus!4v1615851854247!5m2!1sen!2sus"
                    width="100%"
                    height="400"
                    style={{ border: 0 }}
                    allowFullScreen=""
                    loading="lazy"
                ></iframe>
            ),
        },
    ];

    const handleNext = () => {
        setActiveSection((prevSection) => (prevSection + 1) % sections.length);
    };

    const handlePrevious = () => {
        setActiveSection((prevSection) => (prevSection - 1 + sections.length) % sections.length);
    };

    return (
        <div className="slider-container">
            <div className="slider-content">
                <h2>{sections[activeSection].title}</h2>
                <div className="content">{sections[activeSection].content}</div>
                <div className="slider-navigation">
                    <button onClick={handlePrevious}>&#8592; Previous</button>
                    <button onClick={handleNext}>Next &#8594;</button>
                </div>
            </div>
            <div className="aboutImage">
                <img src={logo} alt="Dealership Logo" />
            </div>
        </div>
    );
};

export default About;
