import React, { useState } from 'react';
import axios from 'axios';

const CarForm = ({ addCar }) => { // Accept addCar as a prop
  const [car, setCar] = useState({
    name: '',
    description: '',
    price: '',
    image: null,
  });

  // Handle text input changes
  const handleChange = (e) => {
    setCar({ ...car, [e.target.name]: e.target.value });
  };

  // Handle image file input change
  const handleImageChange = (e) => {
    setCar({ ...car, image: e.target.files[0] }); // Set the selected image file
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare FormData object to send form data including image
    const formData = new FormData();
    formData.append('name', car.name);
    formData.append('description', car.description);
    formData.append('price', car.price);
    formData.append('image', car.image); // Append image file

    try {
      // Send POST request to the API
      const response = await axios.post('http://localhost:5000/car/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Assuming the response contains the added car, you can pass it to the parent component
      addCar(response.data);

      // Reset form after submission
      setCar({ name: '', description: '', price: '', image: null });
    } catch (error) {
      console.error('Error uploading car data:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} action='http://localhost:5000/car/upload' method='post' encType='multipart/form-data'>
      <input
        className='inputBox'
        type="text"
        name="name"
        placeholder="Car Name"
        value={car.name}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="text"
        name="description"
        placeholder="Description"
        value={car.description}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="number"
        name="price"
        placeholder="Price in Rand"
        value={car.price}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="file"
        name="image"
        onChange={handleImageChange}
        required
      />
      <button className='registerButton' type="submit">Add Car</button>
    </form>
  );
};

export default CarForm;
