import React,{useState} from 'react';
import {useNavigate} from 'react-router-dom';

const SignUp = () => {
    const [name, setName]=useState('');
    const [email, setEmail]=useState('');
    const [password, setPassword]=useState('');

    const navigate = useNavigate();

    const collectData = async () => {
        try {
            console.warn(name, email, password);  // Debug the input

            // Make the POST request
            let result = await fetch("http://localhost:5000/register", {
                method: "POST",
                body: JSON.stringify({ name, email, password }),
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            // Check if the response is OK
            if (result.ok) {
                // Convert the response to JSON if it's valid
                let data = await result.json();
                console.warn(data);
            } else {
                console.error(`Error: ${result.statusText} (${result.status})`);
            }
        } catch (error) {
            // Handle any errors from the fetch or JSON parsing
            console.error('Fetch error:', error);
        }
    };


    return (
        <div className='signup'>
            <h1>Register</h1>
            <input className='inputBox' type='text' placeholder='Enter Username'
            value={name} onChange={(e)=>setName(e.target.value)}
            />
            <input className='inputBox' type='text' placeholder='Enter Email'
            value={email} onChange={(e)=>setEmail(e.target.value)}
            />
            <input className='inputBox' type='password' placeholder='Enter Password'
            value={password} onChange={(e)=>setPassword(e.target.value)}
            />
            <button onClick={collectData} className='registerButton' type='button'>Sign Up</button>
        </div>
    )
}

export default SignUp;
