import React, { useState } from 'react';

const CarForm = ({ addCar }) => {
  const [car, setCar] = useState({
    name: '',
    description: '',
    price: '',
    image: '',
  });

  const handleChange = (e) => {
    setCar({ ...car, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setCar({ ...car, image: URL.createObjectURL(e.target.files[0]) });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addCar(car); // Call the addCar function passed from Dashboard
    setCar({ name: '', description: '', price: '', image: '' }); // Reset form
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        className='inputBox'
        type="text"
        name="name"
        placeholder="Car Name"
        value={car.name}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="text"
        name="description"
        placeholder="Description"
        value={car.description}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="number"
        name="price"
        placeholder="Price in Rand"
        value={car.price}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="file"
        name="image"
        onChange={handleImageChange}
        required
      />
      <button className='registerButton' type="submit">Add Car</button>
    </form>
  );
};

export default CarForm;
