import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dash = () => {
  const [cars, setCars] = useState([]);
  const [newCar, setNewCar] = useState({ name: '', description: '', price: '', image: null });

  useEffect(() => {
    axios.get('/api/cars').then((response) => {
      setCars(response.data);
    });
  }, []);

  const handleFileChange = (e) => {
    setNewCar({ ...newCar, image: e.target.files[0] });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCar({ ...newCar, [name]: value });
  };

  const addCar = async () => {
    const formData = new FormData();
    formData.append('name', newCar.name);
    formData.append('description', newCar.description);
    formData.append('price', newCar.price);
    formData.append('image', newCar.image);

    await axios.post('/api/cars', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });

    const response = await axios.get('/api/cars');
    setCars(response.data);
    setNewCar({ name: '', description: '', price: '', image: null });
  };

  return (
    <div>
      <h1>Car Dealership</h1>
      <div>
        <h2>Add a New Car</h2>
        <input
          type="text"
          name="name"
          placeholder="Car Name"
          value={newCar.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newCar.description}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="price"
          placeholder="Price"
          value={newCar.price}
          onChange={handleInputChange}
        />
        <input type="file" onChange={handleFileChange} />
        <button onClick={addCar}>Add Car</button>
      </div>
      <div>
        <h2>Cars Available</h2>
        {cars.map((car) => (
          <div key={car._id}>
            <img src={`/${car.image}`} alt={car.name} style={{ width: '200px', height: '150px' }} />
            <h3>{car.name}</h3>
            <p>{car.description}</p>
            <p>Price: {car.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dash;
