import React, { useState } from 'react';

const CarForm = ({ addCar }) => { // Accept addCar as a prop
  const [car, setCar] = useState({
    name: '',
    description: '',
    price: '',
    image: null,
  });

  const handleChange = (e) => {
    setCar({ ...car, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setCar({ ...car, image: URL.createObjectURL(e.target.files[0]) }); // Display image in CarList
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addCar(car); // Call the addCar function passed down from Dashboard
    setCar({ name: '', description: '', price: '', image: null }); // Reset form after submission
  };

  const collectData = async () => {
    try {
        console.warn(name, email, password);

        // Make the POST request
        let result = await fetch("http://localhost:5000/register", {
            method: "POST",
            body: JSON.stringify({ name, email, password }),
            headers: {
                'Content-Type': 'application/json',
            }
        });

        // Check if the response is OK
        if (result.ok) {
            // Convert the response to JSON if it's valid
            let data = await result.json();
            console.warn(data);
            localStorage.setItem("user", JSON.stringify());
            navigate('/');
        } else {
            console.error(`Error: ${result.statusText} (${result.status})`);
        }
    } catch (error) {
        // Handle any errors from the fetch or JSON parsing
        console.error('Fetch error:', error);
    }
};

  return (
    <form onSubmit={handleSubmit}>
      <input
        className='inputBox'
        type="text"
        name="name"
        placeholder="Car Name"
        value={car.name}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="text"
        name="description"
        placeholder="Description"
        value={car.description}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="number"
        name="price"
        placeholder="Price in Rand"
        value={car.price}
        onChange={handleChange}
        required
      />
      <input
        className='inputBox'
        type="file"
        name="image"
        onChange={handleImageChange}
        required
      />
      <button className='registerButton' type="submit">Add Car</button>
    </form>
  );
};

export default CarForm;
