import React, { useState, useEffect } from 'react';
import CarForm from './CarForm';
import CarList from './CarList';
import * as Components from './Components';
import axios from 'axios';

const Dashboard = () => {
  const [isAdmin, setIsAdmin] = useState(true); // Simulating admin login status
  const [cars, setCars] = useState([]); // State to hold the list of cars

  const addCar = (newCar) => {
    setCars([...cars, newCar]); // Add new car to the list
  };

  // Function to fetch cars from the API
  const fetchCars = async () => {
    try {
      const response = await axios.get('http://localhost:5000/cars'); // Fetching car data from API
      setCars(response.data);  // Store the fetched cars in the state
    } catch (error) {
      console.error('Error fetching cars:', error);
    }
  };

  // Function to delete a car (assumes you have a delete endpoint on the backend)
  const deleteCar = async (index) => {
    const carId = cars[index]._id;  // Assuming your car documents have an `_id` field
    try {
      await axios.delete(`http://localhost:5000/cars/${carId}`);
      setCars(cars.filter((_, i) => i !== index));  // Remove car from state after deletion
    } catch (error) {
      console.error('Error deleting car:', error);
    }
  };

  // Fetch cars when the component mounts
  useEffect(() => {
    fetchCars();
  }, []);

  if (!isAdmin) {
    return <p>Unauthorized access. Admins only.</p>;
  }

  console.log('Cars in Dashboard:', cars); // Debugging line

  return (// Post Method store data from dashboard, Get Method to display data to carlist?????????
    <div className="dashboard"> 
      <Components.Container>
      <h2>Admin Dashboard</h2>
      <CarForm addCar={addCar} /> {/* Pass addCar function to CarForm */}
      <CarList cars={cars}/> {/* Pass cars list and deleteCar function to CarList */}
      </Components.Container>
    </div>
  );
};

export default Dashboard;
