import React, { useState } from 'react';
import axios from 'axios';
import './style.css'; // Importing the style sheet

const Dashboard = () => {
  const [cars, setCars] = useState([]);
  const [activeForm, setActiveForm] = useState(''); // To track which form is active
  const [car, setCar] = useState({ name: '', description: '', price: '', image: null });
  const [carToUpdate, setCarToUpdate] = useState(null);

  // Fetch and set cars (mock implementation for this demo)
  const fetchCars = async () => {
    try {
      const response = await axios.get('http://localhost:5000/cars');
      setCars(response.data);
    } catch (error) {
      console.error('Error fetching cars:', error);
    }
  };

  // Handle form input changes for adding/updating car
  const handleChange = (e) => {
    setCar({ ...car, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setCar({ ...car, image: e.target.files[0] });
  };

  const handleAddCar = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', car.name);
    formData.append('description', car.description);
    formData.append('price', car.price);
    formData.append('image', car.image);

    try {
      const response = await axios.post('http://localhost:5000/car/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      if (response.status === 201) {
        setCars([...cars, response.data]);
        alert('Car added successfully!');
        setCar({ name: '', description: '', price: '', image: null });
        setActiveForm('');
      }
    } catch (error) {
      console.error('Error uploading car data:', error);
    }
  };

  const handleUpdateCar = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', car.name);
    formData.append('description', car.description);
    formData.append('price', car.price);
    if (car.image instanceof File) {
      formData.append('image', car.image);
    }

    try {
      const response = await axios.put(`http://localhost:5000/car/${carToUpdate.id}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      if (response.status === 200) {
        setCars(cars.map((c) => (c.id === response.data.id ? response.data : c)));
        alert('Car updated successfully!');
        setCar({ name: '', description: '', price: '', image: null });
        setActiveForm('');
      }
    } catch (error) {
      console.error('Error updating car:', error);
    }
  };

  const handleDeleteCar = async (carId) => {
    if (window.confirm('Are you sure you want to delete this car?')) {
      try {
        const response = await axios.delete(`http://localhost:5000/car/${carId}`);
        if (response.status === 200) {
          setCars(cars.filter((c) => c.id !== carId));
          alert('Car deleted successfully!');
          setActiveForm('');
        }
      } catch (error) {
        console.error('Error deleting car:', error);
      }
    }
  };

  return (
    <div className="dashboard-container">
      <div className="button-container">
        <button className="toggle-btn" onClick={() => setActiveForm('add')}>Add Car</button>
        <button className="toggle-btn" onClick={() => setActiveForm('update')}>Update Car</button>
        <button className="toggle-btn" onClick={() => setActiveForm('delete')}>Delete Car</button>
      </div>

      {/* Add Car Form */}
      {activeForm === 'add' && (
        <div className="form-container form-3d">
          <h3>Add New Car</h3>
          <form onSubmit={handleAddCar}>
            <input className='inputBox' type="text" name="name" placeholder="Car Name" value={car.name} onChange={handleChange} required />
            <input className='inputBox' type="text" name="description" placeholder="Description" value={car.description} onChange={handleChange} required />
            <input className='inputBox' type="number" name="price" placeholder="Price in Rand" value={car.price} onChange={handleChange} required />
            <input className='inputBox' type="file" name="image" onChange={handleImageChange} required />
            <button className='registerButton' type="submit">Add Car</button>
          </form>
        </div>
      )}

      {/* Update Car Form */}
      {activeForm === 'update' && carToUpdate && (
        <div className="form-container form-3d">
          <h3>Update Car</h3>
          <form onSubmit={handleUpdateCar}>
            <input className='inputBox' type="text" name="name" placeholder="Car Name" value={car.name} onChange={handleChange} required />
            <input className='inputBox' type="text" name="description" placeholder="Description" value={car.description} onChange={handleChange} required />
            <input className='inputBox' type="number" name="price" placeholder="Price in Rand" value={car.price} onChange={handleChange} required />
            <input className='inputBox' type="file" name="image" onChange={handleImageChange} />
            <button className='registerButton' type="submit">Update Car</button>
          </form>
        </div>
      )}

      {/* Delete Car Form */}
      {activeForm === 'delete' && (
        <div className="form-container form-3d">
          <h3>Delete Car</h3>
          {cars.map((car) => (
            <div key={car.id} className="delete-car-container">
              <span>{car.name}</span>
              <button className="registerButton" onClick={() => handleDeleteCar(car.id)}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
