import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // If you're using React Router
import CarDetails from './CarDetails';

const CarList = () => {
  const [cars, setPurchaseCars] = useState([]); // State to hold the list of cars
  const [rentCars, setRentCars] = useState([]); // State to hold the list of cars
  const [searchTerm, setSearchTerm] = useState(''); // State to hold the search term
  const [activePage, setActivePage] = useState('selling-cars'); // State to handle the active page display
  const navigate = useNavigate(); // React Router navigation hook
  // Function to fetch purchased cars from the API
  const fetchPurchaseCars = async () => {
    try {
      const response = await axios.get('http://localhost:5000/purchase/cars'); // Fetching cars data from API
      setPurchaseCars(response.data); // Store the fetched cars in the state
    } catch (error) {
      console.error('Error fetching cars:', error);
    }
  };

  // Function to fetch rent cars from the API
  const fetchRentCars = async () => {
    try {
      const response = await axios.get('http://localhost:5000/rent/cars'); // Fetching cars data from API
      setRentCars(response.data); // Store the fetched cars in the state
    } catch (error) {
      console.error('Error fetching cars:', error);
    }
  };

  // Fetch cars when the component mounts
  useEffect(() => {
    fetchPurchaseCars();
  }, []);

  useEffect(() => {
    fetchRentCars();
  }, []);

  // Function to handle search input change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Function to handle switching between Buy Car and Rent Car
  const handlePageSwitch = (page) => {
    setActivePage(page);
  };

   // Function to handle car detail display
   const saleCarDetail = (car) => {
    // You can either navigate to a new page or show the details in a modal
    // Option 1: Navigate to a new details page with React Router
    navigate(`/cars/${car._id}`, { state: { car } }); // Pass car details as state

    // Option 2: Show the details in a modal (if preferred)
    // You can implement a modal here if you want to show the details without navigation.
    console.log('Displaying car details:', car);
  };

  // Filter cars based on the search term
  const filteredCars = cars.filter((car) =>
    car.make.toLowerCase().includes(searchTerm.toLowerCase())
  );
  const filteredRentCars = rentCars.filter((car) =>
    car.make.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <>
      {/* Navigation Bar with Buy Car and Rent Car Buttons */}
      <div className='car-list-nav'>
        <button
          className={`nav-button ${activePage === 'selling-cars' ? 'active' : ''}`}
          onClick={() => handlePageSwitch('selling-cars')}
        >
          Buy Car
        </button>
        <button
          className={`nav-button ${activePage === 'renting-cars' ? 'active' : ''}`}
          onClick={() => handlePageSwitch('renting-cars')}
        >
          Rent Car
        </button>
      </div>

      {/* Selling Cars Section */}
      {activePage === 'selling-cars' && (
        <div className='selling-cars'>
          <div className="car-list">
            <h2>Buy Car</h2>
            {/* Search bar */}
            <div className="filter-container">
              <input
                type="text"
                placeholder="Search for cars..."
                value={searchTerm}
                onChange={handleSearchChange}
                className="search-bar"
              />
            </div>
            <div className="item-wrapper">
              {filteredCars.length === 0 ? (
                <p>No cars available.</p>
              ) : (
                filteredCars.map((car, index) => (
                  <button key={index} className="item-card"  onClick={() => saleCarDetail(car)}>
                    <img className="item-img" src={car.image} alt={car.make} />
                    <div className="item-details">
                      <div className="item-name">
                        <h4>{car.make}</h4>
                        <p className="item-description">{car.description}</p>
                        <p className="price">R{car.price}</p>
                      </div>
                    </div>
                  </button>
                ))
              )}
            </div>
          </div>
        </div>
      )}

      {/* Renting Cars Section */}
      {activePage === 'renting-cars' && (
        <div className='renting-cars'>
          <div className="car-list">
            <h2>Rent Car</h2>
            {/* Search bar */}
            <div className="filter-container">
              <input
                type="text"
                placeholder="Search for cars..."
                value={searchTerm}
                onChange={handleSearchChange}
                className="search-bar"
              />
            </div>
            <div className="item-wrapper">
              {filteredRentCars.length === 0 ? (
                <p>No cars available.</p>
              ) : (
                filteredRentCars.map((car, index) => (
                  <button key={index} className="item-card">
                    <img className="item-img" src={car.image} alt={car.make} />
                    <div className="item-details">
                      <div className="item-name">
                        <h4>{car.make}</h4>
                        <p className="item-description">{car.description}</p>
                        <p className="price">R{car.price}</p>
                      </div>
                    </div>
                  </button>
                ))
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default CarList;
