import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as Components from './Components';

const SignUp = () => {
    const [isSignIn, setIsSignIn] = useState(true); // State to toggle sign-in and sign-up
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const navigate = useNavigate();

    const collectData = async () => {
        try {
            console.warn(name, email, password);

            let result = await fetch("http://localhost:5000/register", {
                method: "POST",
                body: JSON.stringify({ name, email, password }),
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            if (result.ok) {
                let data = await result.json();
                console.warn(data);
                localStorage.setItem("user", JSON.stringify(data));
                navigate('/');
            } else {
                console.error(`Error: ${result.statusText} (${result.status})`);
            }
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

    return (
        <div className='container'>
            {/* Sign Up */}
            <div className='signup-container'>
                <h1>Create Account</h1>
                <input className='inputBox' type='text' placeholder='Enter Username'
                    value={name} onChange={(e) => setName(e.target.value)} />
                <input className='inputBox' type='text' placeholder='Enter Email'
                    value={email} onChange={(e) => setEmail(e.target.value)} />
                <input className='inputBox' type='password' placeholder='Enter Password'
                    value={password} onChange={(e) => setPassword(e.target.value)} />
                <button onClick={collectData} className='registerButton' type='button'>Sign Up</button>
            </div>

            {/* Sign In */}
            <div className='signin-container'>
                <h1>Sign In</h1>
                <input className='inputBox' type='text' placeholder='Enter Email' />
                <input className='inputBox' type='password' placeholder='Enter Password' />
                <button className='registerButton' type='button'>Sign In</button>
            </div>

            {/* Left Panel for Sign In */}
            <div className='left-panel'>
                <h2>Welcome Back!</h2>
                <p>To keep connected with us, please login with your personal info.</p>
                <button className='signin-toggler' onClick={() => setIsSignIn(true)}>Sign In</button>
            </div>

            {/* Right Panel for Sign Up */}
            <div className='right-panel'>
                <h2>Hello, Friend!</h2>
                <p>Enter your personal details and start your journey with us.</p>
                <button className='signup-toggler' onClick={() => setIsSignIn(false)}>Sign Up</button>
            </div>
        </div>
    );
};

export default SignUp;
