import React, { useState } from 'react';
import CarForm from './CarForm';
import CarList from './CarList';
import * as Components from './Components';

const Dashboard = () => {
  const [isAdmin, setIsAdmin] = useState(true); // Simulating admin login status
  const [cars, setCars] = useState([]); // State to hold the list of cars

  const addCar = (newCar) => {
    setCars([...cars, newCar]); // Add new car to the list
  };

  const deleteCar = (indexToRemove) => {
    const updatedCars = cars.filter((_, index) => index !== indexToRemove);
    setCars(updatedCars);
  };

  if (!isAdmin) {
    return <p>Unauthorized access. Admins only.</p>;
  }

  console.log('Cars in Dashboard:', cars); // Debugging line

  return (// Post Method store data from dashboard, Get Method to display data to carlist?????????
    <div className="dashboard"> 
      <Components.Container>
      <h2>Admin Dashboard</h2>
      <CarForm addCar={addCar} /> {/* Pass addCar function to CarForm */}
      <CarList cars={cars}/> {/* Pass cars list and deleteCar function to CarList */}
      </Components.Container>
    </div>
  );
};

export default Dashboard;
