import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useEffect, useState } from 'react';

const CarDetails = () => {
    const { carId } = useParams(); // Get the car ID from the URL parameters
    const [car, setSaleCarDetails] = useState(null); // State to store car details
    const navigate = useNavigate();

    // Function to fetch car details based on car ID
    const fetchCarDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/purchase/car/6716a43d6b22086445070c70`); // Fetch specific car data from the API
            setCar(response.data); // Store the fetched car details in the state
        } catch (error) {
            console.error('Error fetching car details:', error);
        }
    };

    // Fetch car details when the component mounts
    useEffect(() => {
        fetchCarDetails();
    }, [carId]);

    // Function to go back to the previous page (CarList)
    const goBack = () => {
        navigate(-1); // Navigate back to the previous page
    };

    // Display loader while data is being fetched
    if (!car) {
        return <p>Loading car details...</p>;
    }

    return (
        <div className="car-details-container">
            <button onClick={goBack} className="back-button">Back to Car List</button>
            <div className="car-details-card">
                <img src={car.image} alt={car.name} className="car-image"/>
                <div className="car-info">
                    <h2>{car.name}</h2>
                    <p><strong>Model:</strong> {car.model}</p>
                    <p><strong>Year:</strong> {car.year}</p>
                    <p><strong>Price:</strong> R{car.price}</p>
                    <p><strong>Description:</strong> {car.description}</p>
                    <p><strong>Transmission:</strong> {car.transmission}</p>
                    <p><strong>Fuel Type:</strong> {car.fuelType}</p>
                    <p><strong>Mileage:</strong> {car.mileage} km</p>
                    <p><strong>Color:</strong> {car.color}</p>
                </div>
            </div>
        </div>
    );
};

export default CarDetails;
