import React from 'react';
import {
  Container,
  SignUpContainer,
  SignInContainer,
  Form,
  Title,
  Input,
  Button,
  GhostButton,
  Anchor,
  OverlayContainer,
  Overlay,
  LeftOverlayPanel,
  RightOverlayPanel,
  Paragraph
} from './Components';

const Login = () => {
  const [signIn, setSignIn] = React.useState(true);

  // Toggle between sign in and sign up
  const ToggleEvent = (value) => {
    setSignIn(value);
  };

  return (
    <Container>
      {/* Sign Up Container */}
      <SignUpContainer signinIn={signIn}>
        <Form>
          <Title>Create Account</Title>
          <Input type='text' placeholder='Name' />
          <Input type='email' placeholder='Email' />
          <Input type='password' placeholder='Password' />
          <Button>Sign Up</Button>
        </Form>
      </SignUpContainer>

      {/* Sign In Container */}
      <SignInContainer signinIn={signIn}>
        <Form>
          <Title>Sign In</Title>
          <Input type='email' placeholder='Email' />
          <Input type='password' placeholder='Password' />
          <Anchor href='#'>Forgot your password?</Anchor>
          <Button>Sign In</Button>
        </Form>
      </SignInContainer>

      {/* Overlay */}
      <OverlayContainer signinIn={signIn}>
        <Overlay signIn={signIn}>
          {/* Left Overlay Panel */}
          <LeftOverlayPanel signinIn={signIn}>
            <Title>Welcome Back!</Title>
            <Paragraph>
              To keep connected with Luxury Cars, please login with your personal info.
            </Paragraph>
            <GhostButton onClick={() => ToggleEvent(true)}>Sign In</GhostButton>
          </LeftOverlayPanel>

          {/* Right Overlay Panel */}
          <RightOverlayPanel signIn={signIn}>
            <Title>Hello, Friend!</Title>
            <Paragraph>
              Enter your personal details and start your journey with us today.
            </Paragraph>
            <GhostButton onClick={() => ToggleEvent(false)}>Sign Up</GhostButton>
          </RightOverlayPanel>
        </Overlay>

      </OverlayContainer>

    </Container>
  );
};

export default Login;
